import React from 'react';
import './EventModal.css';

function EventModal({ event, onClose, onReserve }) {
  if (!event) return null;

  return (
    <div className="event-modal-overlay" onClick={onClose}>
      <div className="event-modal-card" onClick={e => e.stopPropagation()}>
        <button className="event-modal-close" onClick={onClose}>&times;</button>
        <h2>{event.name}</h2>
        <div className="event-modal-info">
          <div><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</div>
          <div><strong>Time:</strong> {event.time || '10:00 PM onwards'}</div>
          <div><strong>Location:</strong> {event.location}</div>
          <div><strong>Address:</strong> {event.address || 'See club website for details'}</div>
          <div><strong>Price:</strong> {event.price || 'Entry Free / RSVP Required'}</div>
          <div><strong>Dress Code:</strong> {event.dressCode || 'Smart Casuals'}</div>
          <div><strong>Description:</strong> {event.description}</div>
        </div>
        <button className="event-modal-reserve" onClick={() => onReserve(event)}>
          Reserve Spot
        </button>
      </div>
    </div>
  );
}

export default EventModal;
