import React, { useState } from 'react';
import './App.css';

const faqs = [
  {
    question: 'How can I RSVP for an event?',
    answer: 'Just use the RSVP form on our homepage. You will be redirected to WhatsApp to complete your RSVP.'
  },
  {
    question: 'How do I get on the guest list?',
    answer: 'RSVP through the form or contact us directly via WhatsApp. We’ll confirm your spot!'
  },
  {
    question: 'Do you host private parties?',
    answer: 'Yes! Contact us for custom event packages and private party options.'
  },
  {
    question: 'Where are your events located?',
    answer: 'Our events are held at top venues across the city. Check the event details for specific locations.'
  },
];

function FAQ() {
  const [openIndex, setOpenIndex] = useState(null);
  return (
    <section className="faq-section">
      <h2>Frequently Asked Questions</h2>
      <div className="faq-list">
        {faqs.map((faq, idx) => (
          <div key={idx} className={`faq-item${openIndex === idx ? ' open' : ''}`}> 
            <button className="faq-question" onClick={() => setOpenIndex(openIndex === idx ? null : idx)}>
              {faq.question}
              <span className="faq-toggle">{openIndex === idx ? '-' : '+'}</span>
            </button>
            <div className="faq-answer" style={{ display: openIndex === idx ? 'block' : 'none' }}>
              {faq.answer}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default FAQ;
